import React from 'react';
import Axios from 'axios';

const jwt = require('jsonwebtoken');
const cookie_middleware = require('./middleware/cookie_middleware');


export default class Login extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            users: [],
            username: '',
            password: ''
        }
    }

    // componentDidMount() {
    //     Axios.get('/api/login')
    //         .then((response) => {
    //             this.setState({
    //                 users: response.data,
    //         })})
    //         .catch(error => console.error(error));
    // }

    // componentDidUpdate() {
    // }

    onSubmit() {
        Axios.post('/api/login/register', this.state)
            .then(function (response) {
                window.location.href = '/';
            })
            .catch(function (error) {
                console.error(error)
            })
    }

    onClick() {
        const newPerson = { 
            "username": this.state.username,
            "password": this.state.password
        };
        Axios.post('/api/login/verify', newPerson)
            .then( (response) => {
                window.location.href = '/';
            })
            .catch((error) => {
                console.error(error)
            })
    }

    render() {
        const renderedFriends = [];
        for(let i = 0; i < this.state.users.length; i++ ){
            const user = this.state.users[i];
            renderedFriends.push(
                <div className="friend">
                    {user.username} - {user.password}
                </div>
            )
        }
        return (
            <div>
                <h1>Login Page</h1>

                <h2>Register</h2>
                <label >Username:</label>
                <input type="text" value={this.state.username} onChange={e => this.setState({username: e.target.value})}></input>
                <label >Password:</label>
                <input type="text" value={this.state.password} onChange={e => this.setState({password: e.target.value})}></input>
                <div onClick={() => this.onSubmit()}>Register</div>

                <h2>Login</h2>
                <label >Username:</label>
                <input type="text" value={this.state.username} onChange={e => this.setState({username: e.target.value})}></input>
                <label >Password:</label>
                <input type="text" value={this.state.password} onChange={e => this.setState({password: e.target.value})}></input>
                <button onClick={() => this.onClick()}>Login</button>
            </div>

        )
    }
}
