import './App.css';
import React from 'react';
import Axios from 'axios';
import Navbar from './Navbar';

export default class App extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            postList: [],
            loginUser: null

        }
    }

    navbarDisplay() {
        if(this.state.loginUser){
            return (
                <div className="navBar">
                    <h1>Home Page</h1>
                    <div>{this.state.loginUser}</div>
                    <button>Create New Post</button>
                    <button>Logout</button>
                </div>
            )
        }else{
            return (
                <div className="navBar">
                    <h1>Home Page</h1>
                    <button>Login</button>
                </div>
            )
        }
    }

    componentDidMount() {
        Axios.get('/api/post')
            .then((response) => {
                this.setState({
                    postList: response.data,
            })})
            .catch(error => console.error(error));
    }

    componentDidUpdate() {
    }


    enterSinglePost = (post) => {
        if(post.URL) {
            window.location.href = post.URL;
        }
        else{
            // window.location.href = "post.aspx?id=" + post._id;
            window.location.href = "post?id=" + post._id;
        } 
    }

    render() {
        return (
            <div>
                {this.navbarDisplay()}
                <div className = "titleList">
                      {this.state.postList.map((singlePost,index)=><h3 className="title" key={index} onClick={() => this.enterSinglePost(singlePost)}>{singlePost.title}
                      </h3>)}
                </div>
            </div>

        )
    }
}
